    <script type="text/javascript">
        var map;
        $(function () {

            $('#txt_fname').ForceAlphaOnly({ regex: '[a-zA-Z_ ]' });
            $("#txt_mob").ForceNumericOnly();
            /*Submit*/
            $("#btn_cont").click(function () {
                /*Get Value*/
                var fname, lname, email, phone, addr, pin, count, state, city;
                fname = $("#txt_fname").val();
                mob = $("#txt_mob").val();
                email = $("#txt_email").val();
                sub = $("#txt_sub").val();
                rmk = $("#txt_rmk").val();
                if ((fname == "") || (fname.length < 2)) {
                    $("#error_msg").show('slow');
                    $("#error_msg > p").html('<b>Alert: </b> Enter valid name');
                    hidemeplease();
                    return false;
                }
                else if ((mob == "") || (mob.length < 11)) {
                    $("#error_msg").show('slow');
                    $("#error_msg > p").html('<b>Alert: </b> Enter valid mobile no.');
                    hidemeplease();
                    return false;
                }
                else if ((email == "") || (email.length < 3)) {
                    $("#error_msg").show('slow');
                    $("#error_msg > p").html('<b>Alert: </b> Enter valid e-mail');
                    hidemeplease();
                    return false;
                }
                else if (sub.length < 2) {
                    $("#error_msg").show('slow');
                    $("#error_msg > p").html('<b>Alert: </b> Enter the subject');
                    hidemeplease();
                    return false;
                }
                else {
                    document.getElementById("hid_val").value = "inserer";
                    document.frm.method = "post";
                    document.frm.action = "Contact.php";
                    document.frm.submit();
                }
            });

            $('#test1').gmap3({
                map: {
                    options: {
                        center: [23.06263, 70.025629],
                        zoom: 4
                    }
                },
                marker: {
                    values: [
                        { latLng: [19.06263, 73.025629], data: "<b>Dyntat India</b>", options: { icon: "http://www.dyntatbd.com/images/contactus/Gmap.png"} },
                        { latLng: [27.714078, 85.324069], data: "<b>Dyntat Nepal</b>", options: { icon: "http://www.dyntatbd.com/images/contactus/Gmap.png"} },
                        { latLng: [23.792413, 90.424816], data: "<b>Dyntat Bangladesh</b>", options: { icon: "http://www.dyntatbd.com/images/contactus/Gmap.png"} },
                        { latLng: [26.216189, 50.664093], data: "<b>Dyntat Gulf</b>", options: { icon: "http://www.dyntatbd.com/images/contactus/Gmap.png"} }
                    ],
                    options: {
                        draggable: false
                    },
                    events: {
                        mouseover: function (marker, event, context) {
                            var map = $(this).gmap3("get"),
                                infowindow = $(this).gmap3({ get: { name: "infowindow"} });
                            if (infowindow) {
                                infowindow.open(map, marker);
                                infowindow.setContent(context.data);
                            } else {
                                $(this).gmap3({
                                    infowindow: {
                                        anchor: marker,
                                        options: { content: context.data }
                                    }
                                });
                            }
                        },
                        mouseout: function () {
                            var infowindow = $(this).gmap3({ get: { name: "infowindow"} });
                            if (infowindow) {
                                infowindow.close();
                            }
                        }
                    }
                }
            });
        });

        function validateEmailsimaple() {
            var ema = $("#txt_email").val();
            if (ema != "") {
                if (!validateEmail(ema)) {
                    $("#error_msg").show('slow');
                    $("#error_msg > p").html('<b>Alert: </b> Select valid city');
                    hidemeplease();
                    return false;
                }
            }
        }

        function hidemeplease() {
            setTimeout(function () {
                $("#error_msg").hide('slow');
            }, 5000);
        }

    </script>
